/**
* Class representing a list of slides. Implements the Iterable interface.
*
* @memberof module:libresignage/queue
*/
class SlideArray extends Array {
	/**
	* Filter a SlideArray against a dictionary.
	*
	* If all of the key-value pairs in the supplied dictionary also exist in
	* a Slides data, the Slide is preserved. Otherwise it's filtered out.
	*
	* @param {object} dict The dictionary to compare against.
	*
	* @param {SlideList} The filtered SlideArray object.
	*/
	filter_dict(dict) {
		return this.filter(
			slide => {
				for (let key in dict) {
					if (!slide.has(key) || slide.get(key) !== dict[key]) {
						return false;
					}
				}
				return true;
			}
		);
	}
}
module.exports = SlideArray;
